import { all, fork, take } from "redux-saga/effects";

function* watchLogIn() {
  yield take("LOG_IN");
}

function* watchLogOut() {
  yield take("LOG_OUT");
}
function* watchAddPost() {
  yield take("ADD_POST");
}

export default function* rootSaga() {
  yield all([fork(watchLogIn), fork(watchLogOut, fork(watchAddPost))]);
}
//all 배열안에 있는것들을 한번에 실행
//fork 함수를 실행
