const dummyUser = {
  id: 1,
  nickname: "jangsan",
  Posts: [],
  Followings: [],
  Followers: [],
};

export const initialState = {
  isLoggingIn: false, //로그인 시도중
  isLoggedIn: false,
  isLoggingOut: false, // 로그아웃 시도중
  me: null,
  signUpData: {},
  loginData: {},
};

// action당 3가지씩 Request,Success,Failure
//login action
export const loginRequestAction = (data) => {
  return {
    type: "LOG_IN_REQUEST",
    data,
  };
};

//logout action
export const logoutRequestAction = () => {
  return {
    type: "LOG_OUT_REQUEST",
  };
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case "LOG_IN_REQUEST": {
      return {
        ...state,
        isLoggingIn: true,
      };
    }
    case "LOG_IN_SUCCESS": {
      return {
        ...state,
        isLoggingIn: false,
        isLoggedIn: true,
        me: action.data,
      };
    }
    case "LOG_IN_FAILURE": {
      return {
        ...state,
        isLoggingIn: false,
        isLoggedIn: false,
      };
    }
    case "LOG_OUT_REQUEST": {
      return {
        ...state,
        isLoggingOut: true,
        isLoggedIn: false,
      };
    }
    case "LOG_OUT_SUCCESS": {
      return {
        ...state,
        isLoggingOut: false,
        isLoggedIn: false,
        me: null,
      };
    }
    case "LOG_OUT_FAILURE": {
      return {
        ...state,
        isLoggingOut: false,
      };
    }
    case "SIGN_UP": {
      return {
        ...state,
        signUpData: action.data,
      };
    }
    default: {
      return {
        ...state,
      };
    }
  }
};

export default reducer;
