const dummyUser = {
  id: 1,
  nickname: "jangsan",
  Posts: [],
  Followings: [],
  Followers: [],
};

export const initialState = {
  isLoggedIn: false,
  me: null,
  signUpData: {},
  loginData: {},
};

// action당 3가지씩 Request,Success,Failure
//login action
export const loginRequestAction = (data) => {
  return {
    type: "LOG_IN_REQUEST",
    data,
  };
};
export const loginSuccesstAction = (data) => {
  return {
    type: "LOG_IN_SUCCESS",
    data,
  };
};
export const loginFailureAction = (data) => {
  return {
    type: "LOG_IN_FAILURE",
    data,
  };
};

//logout action
export const logoutRequestAction = () => {
  return {
    type: "LOG_OUT_REQUEST",
  };
};
export const logoutSuccessAction = () => {
  return {
    type: "LOG_OUT_SUCCESS",
  };
};
export const logoutFailureAction = () => {
  return {
    type: "LOG_OUT_FAILURE",
  };
};
export default (state = initialState, action) => {
  switch (action.type) {
    case "LOG_IN_REQUEST": {
      return {
        ...state,
        isLoggedIn: true,
        me: dummyUser,
        loginData: action.data,
      };
    }
    case "LOG_IN_SUCCESS": {
      return {
        ...state,
        isLoggedIn: true,
        me: dummyUser,
        loginData: action.data,
      };
    }
    case "LOG_IN_FAILURE": {
      return {
        ...state,
        isLoggedIn: true,
        me: dummyUser,
        loginData: action.data,
      };
    }
    case "LOG_OUT_REQUEST": {
      return {
        ...state,
        isLoggedIn: false,
        me: null,
      };
    }
    case "LOG_OUT_SUCCESS": {
      return {
        ...state,
        isLoggedIn: false,
        me: null,
      };
    }
    case "LOG_OUT_FAILURE": {
      return {
        ...state,
        isLoggedIn: false,
        me: null,
      };
    }
    case "SIGN_UP": {
      return {
        ...state,
        signUpData: action.data,
      };
    }
    default: {
      return {
        ...state,
      };
    }
  }
};
