export const initialState = {
  mainPosts: [
    {
      id: 1,
      User: {
        id: 1,
        nickname: "jangsan",
      },
      content: "첫번째 게시글 #해시태그 #익스프레스 ",
      Images: [
        {
          src: "https://www.google.com/imgres?imgurl=https%3A%2F%2Fimg.freepik.com%2Ffree-photo%2Fbreathtaking-shot-of-the-sea-under-a-dark-and-purple-sky-filled-with-stars_181624-23013.jpg&tbnid=-_ml3gKq6kK3zM&vet=12ahUKEwi81O__-Iz-AhWamFYBHfx5DbIQMygAegUIARDQAQ..i&imgrefurl=https%3A%2F%2Fkr.freepik.com%2Ffree-photos-vectors%2F%25EB%25B0%25A4%25ED%2595%2598%25EB%258A%2598&docid=axjnK4ycoLqhFM&w=626&h=418&q=%EB%AC%B4%EB%A3%8C%EC%9D%B4%EB%AF%B8%EC%A7%80&hl=ko&ved=2ahUKEwi81O__-Iz-AhWamFYBHfx5DbIQMygAegUIARDQAQ",
        },
        {
          src: "https://www.google.com/imgres?imgurl=https%3A%2F%2Fimg.freepik.com%2Ffree-photo%2Fnight-sky-with-lot-of-shiny-stars-with-blurred-mountain-background_1258-84795.jpg&tbnid=GJLEJTR18ZQYVM&vet=12ahUKEwi81O__-Iz-AhWamFYBHfx5DbIQMygBegUIARDSAQ..i&imgrefurl=https%3A%2F%2Fkr.freepik.com%2Ffree-photos-vectors%2F%25EB%25B0%25A4%25ED%2595%2598%25EB%258A%2598&docid=axjnK4ycoLqhFM&w=626&h=391&q=%EB%AC%B4%EB%A3%8C%EC%9D%B4%EB%AF%B8%EC%A7%80&hl=ko&ved=2ahUKEwi81O__-Iz-AhWamFYBHfx5DbIQMygBegUIARDSAQ",
        },
        {
          src: "https://www.google.com/imgres?imgurl=https%3A%2F%2Fget.pxhere.com%2Fphoto%2Fsky-night-star-milky-way-atmosphere-space-galaxy-night-sky-outer-space-astronomy-milkyway-midnight-astronomical-object-spiral-galaxy-18929.jpg&tbnid=CMbPGXGfywt8SM&vet=12ahUKEwi81O__-Iz-AhWamFYBHfx5DbIQMygCegUIARDUAQ..i&imgrefurl=https%3A%2F%2Fpxhere.com%2Fko%2Fphoto%2F18929&docid=5WO1WEgIHPkFyM&w=1920&h=1280&q=%EB%AC%B4%EB%A3%8C%EC%9D%B4%EB%AF%B8%EC%A7%80&hl=ko&ved=2ahUKEwi81O__-Iz-AhWamFYBHfx5DbIQMygCegUIARDUAQ",
        },
      ],
      Comments: [
        {
          user: {
            nickname: "san",
          },
          content: "안녕하세요",
        },
        {
          user: {
            nickname: "jang",
          },
          content: "hihihihi",
        },
      ],
    },
  ],
  imagePaths: [],
  postAdded: false,
};

const ADD_POST = "ADD_POST";
export const addPost = {
  type: ADD_POST,
};
const dummyPost = {
  id: 2,
  content: "더미데이터입니다.",
  User: {
    id: 1,
    nickname: "제로초",
  },
  Images: [],
  Comments: [],
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_POST:
      return {
        ...state,
        mainPosts: [dummyPost, ...state.mainPosts],
        postAdded: true,
      };
    default:
      return state;
  }
};

export default reducer;
